import React, { createContext, useEffect, useState } from "react";

const Context = createContext({})

export function UserContextProvider ({ children }) {

    const [userLogged, setUserLogged] = useState(null)

    useEffect(() => {

        const getUserLoggedData = () => {
          return window.localStorage.getItem('logedUserMenuApp')
        }
        setUserLogged(getUserLoggedData())
        console.log(userLogged)

      }, []);

    return <Context.Provider value={{userLogged, setUserLogged}}>
        {/*user state sent as Context value parameter*/}
        {children}
    </Context.Provider>
}

export default Context
