const handleErrors = (response) => {
    let resp = response

    if(!resp.ok){
        const errorMessage = {'error': resp.statusText}
        console.log(errorMessage)
        return Promise.reject(errorMessage)
    }   
    const jsonValue = resp.json();
    return Promise.resolve(jsonValue)
}

export default async function getDish(url){
        return await fetch(url, resp =>{
            const jsonValue = resp.json();
            Promise.resolve(jsonValue)
            .then(handleErrors)
        })


}
