import React, { useState } from "react";
import PropTypes from 'prop-types';
import { Navigate } from "react-router-dom";



const NewDish = ({ passToken }) => {


    const [categoriesList, setCategoriesList] = useState([])

    const [dishName, setDishName] = useState();
    const [dishDescription, setdishDescription] = useState();
    const [dishCategory, setdishCategory] = useState();
    const [dishObservation, setdishObservation] = useState();
    const [dishImage, setdishImage] = useState();
    const [dishPrice, setdishPrice] = useState();
    const [dishCurrency, setdishCurrency] = useState();


    useEffect(()=> {
        getCategories()
    }, [])

    let handleChange = (e) => {
        setCategoriesList
    }

    let getCategories = async () => {
        let response = await fetch('/api/categories/')
        let data = await response.json()
        setCategories(data)    
    }

    const handleSubmit = async e => {
        e.preventDefault();

        // dishName
        // dishDescription
        // dishCategory
        // dishObservation
        // dishImage
        // dishPrice
        // dishCurrency

    }

    return (
        <div className="new-dish-form">
            <form className="new-dish-form-form" onSubmit={handleSubmit}>
                <label>
                    <h2>Add New Dish</h2>
                    <p>Name:</p>
                    <input type="text" onChange={e => setDishName(e.target.value)} />
                </label>
                <label>
                    <p>Description:</p>
                    <input type="text"  onChange={e => setdishDescription(e.target.value)}/>
                </label>
                <label>
                    <p>Category:</p>
                    <select value={this.state.value} onChange={e => setdishCategory(e.target.value)}>
                    {categoriesList.map((categorie, index) =>
                        <option key={index} value={categorie.id}>{categorie.name}</option>

                    )}
                </select>
                    {/* <input type="text"  onChange={e => setdishCategory(e.target.value)}/> */}
                </label>
                <label>
                    <p>Observation:</p>
                    <input type="text"  onChange={e => setdishObservation(e.target.value)}/>
                </label>
                <label>
                    <p>Picture:</p>
                    <input type="image"  onChange={e => setdishImage(e.target.value)}/>
                </label>
                <label>
                    <p>Price:</p>
                    <input type="number"  onChange={e => setdishPrice(e.target.value)}/>
                </label>
                <label>
                    <p>Currency:</p>
                    <input type="text"  onChange={e => setdishCurrency(e.target.value)}/>
                </label>
                <p> { errorText }</p>
                <div>
                    <button type="submit">Save</button>
                </div>
            </form>
        </div>

)
}

export default NewDish;
