import React, {useState, useEffect} from "react";
import CategoryItem from '../components/CategoryItem'
import ListItem from '../components/ListItem'


const DishList = () => {


    let category = ''

    let [dishes, setDishes] = useState([])


    useEffect(()=> {
        getDishes()
    }, [])

    let getDishes = async () => {

        let response = await fetch('/api/dishes/')
        let data = await response.json()
        setDishes(data)

    }   

    let  actualCategory = (dish) => {
        category = dish.category.name
    }

    let groupCategory = (index, iteratedCaregory) => {
        let toRender = iteratedCaregory !== category ? <CategoryItem key={index} category={iteratedCaregory} /> : ''
        return toRender
    }

    return (
        <div>
            <div className="dish-list">
                {
                <div>
                    {dishes.map((dish, index) => (

                        <>{groupCategory(index, dish.category.name)}
                        <ListItem key={index} dish={dish} />
                        {actualCategory(dish)} </>


                    ))}
                </div>
                }
            </div>
        </div>
    )
}


export default DishList;
