import React, {useState, useEffect} from "react";
import CategoryItem from '../components/CategoryItem'
import ListItem from '../components/ListItem'


const DishList = () => {


    let category = ''

    let [dishes, setDishes] = useState([])


    useEffect(()=> {
        getDishes()
    }, [])

    let getDishes = async () => {

        let response = await fetch('/api/dishes/')
        let data = await response.json()
        setDishes(data)

    }

    let getCategory = async (categoryUrl) => {
        let response = await fetch(categoryUrl)
        let data = await response.json()
        return data.name
    }

    let  actualCategory = (dish) => {
        category = dish.category.name
    }

    let groupCategory = (iteratedCaregory) => {
        console.log(iteratedCaregory.value)
        let toRender = iteratedCaregory !== category ? <CategoryItem category={iteratedCaregory} /> : ''
        return toRender
    }

    return (
        <div>
            <div className="dish-list">
                {
                <div>
                    {dishes.map((dish, index) => (

                        <>{groupCategory(getCategory(dish.category))}
                        <ListItem key={index} dish={dish} />
                        {actualCategory(getCategory(dish.category))} </>


                    ))}
                </div>
                }
            </div>
        </div>
    )
}


export default DishList;
