import React, {useState, useEffect} from "react";
import ListItem from "./ListItem";

const CategoryItem = ({ category }) => {

    const [dishesVisible, setDishesVisible] = useState(false)
    const [dishes, setDishes] = useState([])

    useEffect(()=>{
        setDishes(category.dishes)    
    }, [category])

    return (
        <div>
        <button className="dish-category" onClick={() => setDishesVisible(!dishesVisible)}>
        <div>
            <h3>{ category.name }</h3>

        </div>
        </button>
        <>
        {/* {dishes.map((dish)=>(console.log(dish)))} */}
        {dishes.map((dish, index)=>{
            console.log(dish, "url desde categoryItems")
            return <ListItem key={index} visible={dishesVisible} url={dish} />
        })
        }
        </>
        </div>
    )
}

export default CategoryItem;
