    const [dishesVisible, setDishesVisible] = useState(false)
    //const [dishes, setDishes] = useState([])

    useEffect(()=>{

    }, [dishesVisible])

    return (
        <div>
        <button className="dish-category" onClick={() => setDishesVisible(!dishesVisible)}>
        <div>
            <h3>{ category.name }</h3>

        </div>
        </button>
        <>
        {category.dishes.map((dish)=>(console.log(dish)))}
        {category.dishes.map((dish, index)=>(
        <ListItem key={index} visible={dishesVisible} url={"/api/dishes/1/"} />
        ))
        }
        </>
        </div>
    )
}

export default CategoryItem;
