import React, {useState, useEffect} from "react";
import getDish from "../services/getDish";
import ListItem from "./ListItem";

const CategoryItem = ({ category }) => {

    const [dishesVisible, setDishesVisible] = useState(false)
    const [dishes, setDishes] = useState([])

    // useEffect(()=> {

    // }

    //}, [category, dishesVisible])

    if (dishes.length === 0){
        getDish(category)
        .then(async data =>{
            let dishList = data.map((dish, index) => <ListItem key={index.toString()} visible={dishesVisible} url={dish} />)
            console.log(dishList)
            setDishes( await dishList)
    })
}

    // return <ListItem key={index.toString()} visible={dishesVisible} url={dish} />
    return (
        <div>
        <button className="dish-category" onClick={() => setDishesVisible(!dishesVisible)}>
        <div>
            <h3>{ category.name }</h3>

        </div>

        {dishes}

        </button>
        </div>
    )
}

export default CategoryItem;
