import React, {useState, useEffect} from "react";
import CategoryItem from '../components/CategoryItem'
import ListItem from '../components/ListItem'


const DishList = () => {

    let [dishes, setDishes] = useState([])
    let [category, setCategory] = useState('')

    useEffect(()=> {
        getDishes()
        groupCategory()
    }, [])

    let getDishes = async () => {

        let response = await fetch('/api/dishes/')
        let data = await response.json()
        setDishes(data)

    }

    let groupCategory = (iteratedCaregory) => {
        console.log(iteratedCaregory)
        let toRender = iteratedCaregory !== category ? <CategoryItem category={iteratedCaregory} /> : ''
        setCategory(iteratedCaregory)
        console.log(toRender)
        return toRender
    }


    return (
        <div>
            <div className="dish-list">
                {
                <div>
                    {dishes.map((dish, index) => (

                        <>{groupCategory(dish.category.name)}
                        <ListItem key={index} dish={dish} />
                        </>
                    ))}
                </div>
                }
            </div>
        </div>
    )
}


export default DishList;
