const handleErrors = (response) => {
    if(!response.ok){
        const errorMessage = {'error': response.statusText}
        return errorMessage
    }   
}

export default async function getDish(url){
    return await fetch(url)
        .then(handleErrors)
        .then(res => res.json())              
}
